package PP.pp;

import org.junit.Test;

public class odd {
	
	//Odd Number

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=10;
		for(int i=1; i<=n; i=i+2) {
			System.out.println(i);
		}

	}
	// Even Number
	@Test
	  public void evenNumber() {
		int n=10;
		for(int i=2; i<=n; i=i+2) {
			System.out.println(i);
		}
	  }
}
  